import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

function MyPlanDetail() {
  const { state } = useLocation();
  const plan = state && state.plan;
  const [newTodo, setNewTodo] = useState('');
  const [showInput, setShowInput] = useState(false);

  const handleInputChange = (event) => {
    setNewTodo(event.target.value);
  };

  const handleInputSubmit = async () => {
    try {
      await axios.post('/api/updateTodo', { planId: plan.id, newTodo });
      // 성공적으로 업데이트된 경우, 여기에 추가적인 작업을 할 수 있습니다.
      // 예: 화면을 다시 불러오거나, 성공 메시지를 표시합니다.
      console.log('할일이 성공적으로 업데이트되었습니다.');
    } catch (error) {
      console.error('할일 업데이트 실패:', error);
      // 에러 처리를 원하는 대로 구현하세요.
    }
  };

  return (
    <div className="travel-plan-details">
      <h2>나의 여행 계획 상세</h2>
      {plan && (
        <>
          <div className="plan-section">
            <h3>저장 제목</h3>
            <p>{plan.saveTitle}</p>
          </div>
          <div className="plan-section">
            <h3>여행 시작 날짜</h3>
            <p>{plan.firstDate}</p>
          </div>
          <div className="plan-section">
            <h3>여행 종료 날짜</h3>
            <p>{plan.endDate}</p>
          </div>
          <div className="plan-section">
            <h3>출발지</h3>
            <p>{plan.firstPlace}</p>
          </div>
          <div className="plan-section">
            <h3>도착지</h3>
            <p>{plan.endPlace}</p>
          </div>
          <div className="plan-section">
            <h3>할일</h3>
            <p>{plan.todo}</p>
            <button onClick={() => setShowInput(true)}>입력</button>
          </div>
          {showInput && (
            <div className="plan-section">
              <input
                type="text"
                value={newTodo}
                onChange={handleInputChange}
                placeholder="새로운 할일 입력"
              />
              <button onClick={handleInputSubmit}>확인</button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default MyPlanDetail;
