// src/components/js/RecommendedPlaces.jsx

import React from 'react';
import '../css/RecommendedPlaces.css';
import LoadMoreButton from './LoadMoreButton';
import { formatDate } from './TrainList'; // TrainList 파일에서 가져오기

const RecommendedPlaces = ({ data, loadMore, loading, view, selectedItems, setSelectedItems, selectedTrains = [], setSelectedTrains }) => {

  const handleAddClick = (item) => {
    let category;
    switch (item.contenttypeid) {
      case '12':
        category = 'attractions';
        break;
      case '32':
        category = 'hotels';
        break;
      case '39':
        category = 'restaurants';
        break;
      default:
        return;
    }

    // Ensure the category array exists
    if (!selectedItems[category]) {
      setSelectedItems(prevState => ({
        ...prevState,
        [category]: []
      }));
    }

    // Check if the item already exists in the category array
    const existingItem = selectedItems[category]?.find(selectedItem => selectedItem.contentid === item.contentid);
    if (!existingItem) {
      const newItem = { ...item, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) };
      setSelectedItems(prevState => ({
        ...prevState,
        [category]: [...(prevState[category] || []), newItem]
      }));
    }
  };

  const handleRemoveClick = (contentid, category) => {
    setSelectedItems(prevState => ({
      ...prevState,
      [category]: prevState[category].filter(item => item.contentid !== contentid)
    }));
  };

  const isItemSelected = (contentid, category) => {
    return selectedItems[category] && selectedItems[category].some(item => item.contentid === contentid);
  };

  const handleTrainRemoveClick = (trainno) => {
    setSelectedTrains(selectedTrains.filter(train => train.trainno !== trainno));
  };

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (!data || data.length === 0) {
    return <div>데이터가 없습니다</div>;
  }

  return (
    <div>
      <h1>추천 장소</h1>
      <div className="recommended-places">
        {data.map(item => (
          <div key={item.contentid} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="recommended-image"
            />
            {isItemSelected(item.contentid, 'attractions') ||
            isItemSelected(item.contentid, 'hotels') ||
            isItemSelected(item.contentid, 'restaurants') ? (
              <button onClick={() => handleRemoveClick(item.contentid, 'attractions')}>체크 해제</button>
            ) : (
              <button onClick={() => handleAddClick(item)}>체크</button>
            )}
          </div>
        ))}
      </div>
      <LoadMoreButton onClick={loadMore} />
      <div>
        <h1>Selected Attractions</h1>
        <div className="selected-items">
          {selectedItems.attractions && selectedItems.attractions.map((item) => (
            <div key={item.uniqueId} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="selected-image"
              />
              <button onClick={() => handleRemoveClick(item.contentid, 'attractions')}>체크</button>
            </div>
          ))}
        </div>
        <h1>Selected Hotels</h1>
        <div className="selected-items">
          {selectedItems.hotels && selectedItems.hotels.map((item) => (
            <div key={item.uniqueId} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="selected-image"
              />
              <button onClick={() => handleRemoveClick(item.contentid, 'hotels')}>체크</button>
            </div>
          ))}
        </div>
        <h1>Selected Restaurants</h1>
        <div className="selected-items">
          {selectedItems.restaurants && selectedItems.restaurants.map((item) => (
            <div key={item.uniqueId} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="selected-image"
              />
              <button onClick={() => handleRemoveClick(item.contentid, 'restaurants')}>체크</button>
            </div>
          ))}
        </div>
        <h1>Selected Trains</h1>
        <div className="selected-items">
          {selectedTrains && selectedTrains.map((train) => (
            <div key={train.uniqueId} className="card">
              <h2>{train.traingradename}</h2>
              <p>출발: {train.depplacename}역 {formatDate(train.depplandtime)}</p>
              <p>도착: {train.arrplacename}역 {formatDate(train.arrplandtime)}</p>
              <p>요금: {train.adultcharge}원</p>
              <p>열차번호: {train.trainno}</p>
              <button onClick={() => handleTrainRemoveClick(train.trainno)}>체크</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RecommendedPlaces;
