import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';


function MyPlanDetail() {
  const { state } = useLocation();
  const [plan, setPlan] = useState(state && state.plan);
  const [newTodo, setNewTodo] = useState('');
  const [additionalData, setAdditionalData] = useState({
    accommodation: null,
    restaurant: null,
    transportation: null,
    tour: null,
  });

  useEffect(() => {
    async function fetchPlan() {
      try {
        const response = await axios.get(`/api/users/${plan.saveNo}`);
        setPlan(response.data);
      } catch (error) {
        console.error('여행 계획 상세 정보를 불러오는 중 오류 발생:', error);
      }
    }

    async function fetchAdditionalData() {
      try {
        const [accommodationResponse, restaurantResponse, transportationResponse, tourResponse] = await Promise.all([
          axios.get(`/api/accommodations/${plan.saveNo}`),
          axios.get(`/api/restaurants/${plan.saveNo}`),
          axios.get(`/api/transportations/${plan.saveNo}`),
          axios.get(`/api/tours/${plan.saveNo}`)
        ]);

        setAdditionalData({
          accommodation: accommodationResponse.data,
          restaurant: restaurantResponse.data,
          transportation: transportationResponse.data,
          tour: tourResponse.data,
        });
      } catch (error) {
        console.error('Error fetching additional data:', error);
      }
    }

    if (plan) {
      fetchPlan();
      fetchAdditionalData();
    }
  }, [plan]);

  const handleUpdateTodo = async () => {
    try {
      await axios.put(`/api/users/${plan.saveNo}/todo`, { todo: newTodo });
      setPlan({ ...plan, todo: newTodo });
    } catch (error) {
      console.error('할일 업데이트 실패:', error);
    }
  };

  return (
    <div className="myplandetail-container">
      <div className="myplandetail-card">
        <h2 className="myplandetail-text-center">나의 여행 계획 상세</h2>
        {plan && (
          <>
            <div className="myplandetail-section">
              <h3>저장 제목</h3>
              <p>{plan.saveTitle}</p>
            </div>
            <div className="myplandetail-section">
              <h3>여행 시작 날짜</h3>
              <p>{plan.firstDate}</p>
            </div>
            <div className="myplandetail-section">
              <h3>여행 종료 날짜</h3>
              <p>{plan.endDate}</p>
            </div>
            <div className="myplandetail-section">
              <h3>출발지</h3>
              <p>{plan.firstPlace}</p>
            </div>
            <div className="myplandetail-section">
              <h3>도착지</h3>
              <p>{plan.endPlace}</p>
            </div>
            <div className="myplandetail-section">
              <h3>할일</h3>
              <p>{plan.todo}</p>
              <input
                type="text"
                className="form-control"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="새로운 할일 입력"
              />
              <button className="myplandetail-btn btn btn-primary" onClick={handleUpdateTodo}>입력</button>
            </div>
            {additionalData.accommodation && (
              <div className="myplandetail-section">
                <h3>숙박 정보</h3>
                <p>{additionalData.accommodation.accName}</p>
                <p>{additionalData.accommodation.accAddress}</p>
              </div>
            )}
            {additionalData.restaurant && (
              <div className="myplandetail-section">
                <h3>레스토랑 정보</h3>
                <p>{additionalData.restaurant.resName}</p>
                <p>{additionalData.restaurant.resAddress}</p>
              </div>
            )}
            {additionalData.transportation && (
              <div className="myplandetail-section">
                <h3>교통 수단 정보</h3>
                <p>{additionalData.transportation.transName}</p>
                <p>{additionalData.transportation.firstPlace} → {additionalData.transportation.endPlace}</p>
              </div>
            )}
            {additionalData.tour && (
              <div className="myplandetail-section">
                <h3>관광 정보</h3>
                <p>{additionalData.tour.tourName}</p>
                <p>{additionalData.tour.tourAddress}</p>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default MyPlanDetail;
