import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import 'react-datepicker/dist/react-datepicker.css';
import './App.css';
import TravelDate from "./components/js/TravelDate";
import TravelDetails from "./components/js/TravelDetails";
import Login from "./components/js/Login";
import SignUp from "./components/js/SignUp";

function App() {
    const [loggedInUser, setLoggedInUser] = useState(null);

    useEffect(() => {
        // 세션에서 사용자 정보 가져오기
        const userFromSession = localStorage.getItem('user');
        if (userFromSession) {
            setLoggedInUser(JSON.parse(userFromSession));
        }
    }, []);

    const handleLogin = (user) => {
        // 로그인 성공 시 세션에 사용자 정보 저장
        localStorage.setItem('user', JSON.stringify(user));
        setLoggedInUser(user);
    };

    const handleLogout = () => {
        // 로그아웃 시 세션에서 사용자 정보 제거
        localStorage.removeItem('user');
        setLoggedInUser(null);
    };

    return (
        <Router>
            <div className="App">
                <div className="auth-links">
                    {loggedInUser ? (
                        <button onClick={handleLogout}>로그아웃</button>
                    ) : (
                        <>
                            <Link to="/login">로그인</Link> | <Link to="/signup">회원가입</Link>
                        </>
                    )}
                </div>
                <h1>YOUR PLANNER</h1>
                <h4>유플과 시작하는 여행 !</h4>

                <Routes>
                    <Route path="/" element={<TravelDate />} />
                    <Route path="/details" element={<TravelDetails />} />
                    <Route
                        path="/login"
                        element={<Login onLogin={handleLogin} />}
                    />
                    <Route path="/signup" element={<SignUp />} />
                    {loggedInUser ? (
                        <Route path="/" element={<Navigate to="/details" />} />
                    ) : null}
                </Routes>
            </div>
        </Router>
    );
}

export default App;
