import React, { useState, useEffect } from 'react';
import RecommendedPlaces from "./RecommendedPlaces";
import LoadMoreButton from './LoadMoreButton';

const fetchAttractionsData = async (pageNumber, areaCode, sigunguCode) => {
    const url = `http://apis.data.go.kr/B551011/KorService1/areaBasedList1?serviceKey=5uJ1mFn4tOfEwReTW3dupjd4w2n5kEHO5nciT%2BDVGAVWTl90sysBKbMTIlIxLW5lCPo1VmpZ%2FXggxU84GhG81g%3D%3D&pageNo=${pageNumber}&numOfRows=10&MobileApp=AppTest&MobileOS=ETC&arrange=A&areaCode=${areaCode}&sigunguCode=${sigunguCode}`;
    return fetch(url);
};

const fetchHotelsData = async (pageNumber, areaCode, sigunguCode) => {
    const url = `http://apis.data.go.kr/B551011/KorService1/areaBasedList1?serviceKey=5uJ1mFn4tOfEwReTW3dupjd4w2n5kEHO5nciT%2BDVGAVWTl90sysBKbMTIlIxLW5lCPo1VmpZ%2FXggxU84GhG81g%3D%3D&pageNo=${pageNumber}&numOfRows=10&MobileApp=AppTest&MobileOS=ETC&arrange=B&areaCode=${areaCode}&sigunguCode=${sigunguCode}&contentTypeId=32`;
    return fetch(url);
};

const fetchRestaurantData = async (pageNumber, areaCode, sigunguCode) => {
    const url = `http://apis.data.go.kr/B551011/KorService1/areaBasedList1?serviceKey=5V%2BytDDcz11Mfxc3tREUmoX6wOvDmA3oIaBkQfhB%2Bo%2B4vBWem3h6eQhKVvJuiJvpVonGtnuRqU6A83YSSBAh8A%3D%3D&pageNo=${pageNumber}&numOfRows=10&MobileApp=AppTest&MobileOS=ETC&arrange=A&areaCode=${areaCode}&sigunguCode=${sigunguCode}&contentTypeId=39`;
    return fetch(url);
};

const fetchData = async (pageNumber, type, areaCode, sigunguCode) => {
    switch (type) {
        case 'attractions':
            return fetchAttractionsData(pageNumber, areaCode, sigunguCode);
        case 'hotels':
            return fetchHotelsData(pageNumber, areaCode, sigunguCode);
        case 'restaurant':
            return fetchRestaurantData(pageNumber, areaCode, sigunguCode);
        default:
            return null;
    }
};

const ListContainer = ({ areaCode, sigunguCode, view }) => {
    const [data, setData] = useState([]);
    const [page, setPage] = useState(1);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setData([]); // View가 변경될 때마다 데이터를 초기화합니다.
        setPage(1);
        fetchData(1, view, areaCode, sigunguCode); // 초기 데이터를 가져옵니다.
    }, [view, areaCode, sigunguCode]);

    useEffect(() => {
        if (page > 1) {
            fetchData(page, view, areaCode, sigunguCode); // 페이지가 변경될 때만 더 많은 데이터를 가져옵니다.
        }
    }, [page, view, areaCode, sigunguCode]);

    const loadMore = () => {
        setPage(prevPage => prevPage + 1);
    };

    return (
        <div>
            {/*RecommendedPlaces에 값 전달*/}
            <RecommendedPlaces data={data} loadMore={loadMore} loading={loading} />
        </div>
    );
};

export default ListContainer;
