import React, { useState } from 'react';
import '../css/RecommendedPlaces.css';
import LoadMoreButton from './LoadMoreButton';
import TrainList from './TrainList';

const RecommendedPlaces = ({
    data, loadMore, loading, view, selectedItems, setSelectedItems,
    depPlaceId, arrPlaceId, startDate
}) => {

    const handleAddClick = (item) => {
        let category;
        switch (item.contenttypeid) {
            case '12':
                category = 'attractions';
                break;
            case '32':
                category = 'hotels';
                break;
            case '39':
                category = 'restaurants';
                break;
            default:
                return;
        }

        if (!selectedItems[category]) {
            setSelectedItems(prevState => ({
                ...prevState,
                [category]: []
            }));
        }

        const existingItem = selectedItems[category]?.find(selectedItem => selectedItem.contentid === item.contentid);
        if (!existingItem) {
            const newItem = { ...item, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) };
            setSelectedItems(prevState => ({
                ...prevState,
                [category]: [...(prevState[category] || []), newItem]
            }));
        }
    };

    const handleRemoveClick = (contentid, category) => {
        setSelectedItems(prevState => ({
            ...prevState,
            [category]: prevState[category].filter(item => item.contentid !== contentid)
        }));
    };

    const isItemSelected = (contentid, category) => {
        return selectedItems[category] && selectedItems[category].some(item => item.contentid === contentid);
    };

    const handleAddTrainClick = (train) => {
        if (!selectedItems.trains) {
            setSelectedItems(prevState => ({
                ...prevState,
                trains: []
            }));
        }

        const existingTrain = selectedItems.trains?.find(selectedTrain => selectedTrain.trainno === train.trainno);
        if (!existingTrain) {
            const newTrain = { ...train, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) };
            setSelectedItems(prevState => ({
                ...prevState,
                trains: [...(prevState.trains || []), newTrain]
            }));
        }
    };

    const handleRemoveTrainClick = (trainno) => {
        setSelectedItems(prevState => ({
            ...prevState,
            trains: prevState.trains.filter(train => train.trainno !== trainno)
        }));
    };

    const isTrainSelected = (trainno) => {
        return selectedItems.trains && selectedItems.trains.some(train => train.trainno === trainno);
    };

    return (
        <div>
            <div className="card-container">
                {data.map((item, index) => {
                    let category;
                    switch (item.contenttypeid) {
                        case '12':
                            category = 'attractions';
                            break;
                        case '32':
                            category = 'hotels';
                            break;
                        case '39':
                            category = 'restaurants';
                            break;
                        default:
                            return null;
                    }
                    return (
                        <div key={item.contentid + index} className="card">
                            <h2>{item.title}</h2>
                            <p>{item.addr1}</p>
                            <img
                                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                                alt={item.title}
                                className="hotel-image"
                            />
                            {isItemSelected(item.contentid, category) ? (
                                <button onClick={() => handleRemoveClick(item.contentid, category)}>체크</button>
                            ) : (
                                <button onClick={() => handleAddClick(item)}>추가</button>
                            )}
                        </div>
                    );
                })}
            </div>
            <LoadMoreButton loadMore={loadMore} loading={loading} />

            <div>
                <h1>Selected Attractions</h1>
                <div className="selected-items">
                    {selectedItems.attractions && selectedItems.attractions.map((item) => (
                        <div key={item.uniqueId} className="card">
                            <h2>{item.title}</h2>
                            <p>{item.addr1}</p>
                            <img
                                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                                alt={item.title}
                                className="selected-image"
                            />
                            <button onClick={() => handleRemoveClick(item.contentid, 'attractions')}>삭제</button>
                        </div>
                    ))}
                </div>

                <h1>Selected Hotels</h1>
                <div className="selected-items">
                    {selectedItems.hotels && selectedItems.hotels.map((item) => (
                        <div key={item.uniqueId} className="card">
                            <h2>{item.title}</h2>
                            <p>{item.addr1}</p>
                            <img
                                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                                alt={item.title}
                                className="selected-image"
                            />
                            <button onClick={() => handleRemoveClick(item.contentid, 'hotels')}>삭제</button>
                        </div>
                    ))}
                </div>

                <h1>Selected Restaurants</h1>
                <div className="selected-items">
                    {selectedItems.restaurants && selectedItems.restaurants.map((item) => (
                        <div key={item.uniqueId} className="card">
                            <h2>{item.title}</h2>
                            <p>{item.addr1}</p>
                            <img
                                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                                alt={item.title}
                                className="selected-image"
                            />
                            <button onClick={() => handleRemoveClick(item.contentid, 'restaurants')}>삭제</button>
                        </div>
                    ))}
                </div>

                <h1>Selected Trains</h1>
                <div className="selected-items">
                    {selectedItems.trains && selectedItems.trains.map((train) => (
                        <div key={train.uniqueId} className="card">
                            <p>{train.traingradename} / 출발: {train.depplacename}역 {train.depplandtime} / 도착: {train.arrplacename}역 {train.arrplandtime} / 요금: {train.adultcharge}원 / 열차번호: {train.trainno}</p>
                            <button onClick={() => handleRemoveTrainClick(train.trainno)}>삭제</button>
                        </div>
                    ))}
                </div>

                <TrainList
                    depPlaceId={depPlaceId}
                    arrPlaceId={arrPlaceId}
                    startDate={startDate}
                    addTrain={handleAddTrainClick}
                    removeTrain={handleRemoveTrainClick}
                    isTrainSelected={isTrainSelected}
                />
            </div>
        </div>
    );
};

export default RecommendedPlaces;
