import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../css/MyPlanDetail.css';

function MyPage() {
  const [data, setData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/users/saves');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleShowDetails = (plan) => {
    navigate('/MyPlanDetail', { state: { plan, data } });
  };

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div className="myplan-container">
      <h2>마이페이지</h2>
      <div className="plan-list">
        {data.saves.map((plan, index) => (
          <div key={index} className="plan-details">
            <p><strong>저장 제목:</strong> {plan.saveTitle}</p>
            <p><strong>여행 시작 날짜:</strong> {plan.firstDate}</p>
            <p><strong>여행 종료 날짜:</strong> {plan.endDate}</p>
            <p><strong>출발지:</strong> {plan.firstPlace}</p>
            <p><strong>도착지:</strong> {plan.endPlace}</p>
            <button type="button" onClick={() => handleShowDetails(plan)}>상세 보기</button>
          </div>
        ))}
      </div>
    </div>
  );
}

function MyPlanDetail() {
  const { state } = useLocation();
  const { plan, data } = state;

  const [newTodo, setNewTodo] = useState('');

  const handleUpdateTodo = async () => {
    try {
      await axios.put(`/api/users/${plan.saveNo}/todo`, { todo: newTodo });
      // 새로운 할일을 업데이트한 후에 즉시 UI에 반영
      plan.todo = newTodo;
    } catch (error) {
      console.error('Failed to update todo:', error);
    }
  };

  return (
    <div className="myplandetail-container">
      <div className="myplandetail-card">
        <h2 className="myplandetail-text-center">나의 여행 계획 상세</h2>
        <div className="myplandetail-section">
          <h3>저장 제목</h3>
          <p>{plan.saveTitle}</p>
        </div>
        <div className="myplandetail-section">
          <h3>여행 시작 날짜</h3>
          <p>{plan.firstDate}</p>
        </div>
        <div className="myplandetail-section">
          <h3>여행 종료 날짜</h3>
          <p>{plan.endDate}</p>
        </div>
        <div className="myplandetail-section">
          <h3>출발지</h3>
          <p>{plan.firstPlace}</p>
        </div>
        <div className="myplandetail-section">
          <h3>도착지</h3>
          <p>{plan.endPlace}</p>
        </div>
        <div className="myplandetail-section">
          <h3>할일</h3>
          <p>{plan.todo}</p>
          <input
            type="text"
            className="form-control"
            value={newTodo}
            onChange={(e) => setNewTodo(e.target.value)}
            placeholder="새로운 할일 입력"
          />
          <button className="myplandetail-btn btn btn-primary" onClick={handleUpdateTodo}>입력</button>
        </div>
        {/* 추가 데이터 표시 */}
        <div className="myplandetail-section">
          <h3>숙박 정보</h3>
          {data[`accommodations_${plan.saveNo}`]?.map((acc, idx) => (
            <div key={idx}>
              <p>{acc.accName}</p>
              <p>{acc.accAddress}</p>
            </div>
          ))}
        </div>
        <div className="myplandetail-section">
          <h3>레스토랑 정보</h3>
          {data[`restaurants_${plan.saveNo}`]?.map((res, idx) => (
            <div key={idx}>
              <p>{res.resName}</p>
              <p>{res.resAddress}</p>
            </div>
          ))}
        </div>
        <div className="myplandetail-section">
          <h3>교통 수단 정보</h3>
          {data[`transportations_${plan.saveNo}`]?.map((trans, idx) => (
            <div key={idx}>
              <p>{trans.transName}</p>
              <p>{trans.firstPlace} → {trans.endPlace}</p>
            </div>
          ))}
        </div>
        <div className="myplandetail-section">
          <h3>관광 정보</h3>
          {data[`tours_${plan.saveNo}`]?.map((tour, idx) => (
            <div key={idx}>
              <p>{tour.tourName}</p>
              <p>{tour.tourAddress}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MyPage;
