import React from 'react';
import { useLocation } from 'react-router-dom';
import TrainCode from './TrainCode';
import LocationBasedList from './LocationBasedList';
import TrainList from './TrainList';
import RecommendedPlaces from './RecommendedPlaces';
import DummyLocationBasedList from './DummyLocationBasedList';

const TravelDetails = () => {
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const startDate = params.get('startDate');
    const endDate = params.get('endDate');
    let departure = params.get('departure');
    let destination = params.get('destination');
    let destination2 = params.get('destination');

    const regionMapping = {
        "서울": "1",
        "인천": "2",
        "대전": "3",
        "대구": "4",
        "광주": "5",
        "부산": "6",
        "울산": "7",
        "세종": "8",
        "경기도": "31",
        "강원도": "32",
        "충청북도": "33",
        "충청남도": "34",
        "경상북도": "35",
        "경상남도": "36",
        "전라북도": "37",
        "전라남도": "38",
        "제주도": "39"
    };

    const departureCodes = TrainCode[departure] || [];
    const destinationCodes = TrainCode[destination] || [];

    // destination 값을 rnum 값으로 변환
    if (regionMapping[destination2]) {
        destination2 = regionMapping[destination2];
    }

    const [selectedItems, setSelectedItems] = useState({
        attractions: [],
        hotels: [],
        restaurants: [],
        trains: []
    });

    const addTrain = (train) => {
        setSelectedItems((prevState) => {
            if (!prevState.trains.some(t => t.trainno === train.trainno)) {
                return {
                    ...prevState,
                    trains: [...prevState.trains, { ...train, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) }]
                };
            }
            return prevState;
        });
    };

    const removeTrain = (trainno) => {
        setSelectedItems((prevState) => ({
            ...prevState,
            trains: prevState.trains.filter(train => train.trainno !== trainno)
        }));
    };

    const isTrainSelected = (trainno) => {
        return selectedItems.trains.some(train => train.trainno === trainno);
    };

    return (
        <div>
            <h2>Travel Details</h2>
            <p><strong>Departure:</strong> {departure}</p>
            <p><strong>Destination:</strong> {destination}</p>
            <p><strong>Start Date:</strong> {startDate}</p>
            <p><strong>End Date:</strong> {endDate}</p>

            {departureCodes.map(depCode =>
                destinationCodes.map(destCode => (
                    <TrainList
                        key={`${depCode}-${destCode}`}
                        depPlaceId={depCode}
                        arrPlaceId={destCode}
                        startDate={startDate}
                        addTrain={addTrain}
                        removeTrain={removeTrain}
                        isTrainSelected={isTrainSelected}
                    />
                ))
            )}
            <LocationBasedList destination={destination} />
            <DummyLocationBasedList destination2={destination2} />
            <RecommendedPlaces
                depPlaceId={departureCodes[0]}
                arrPlaceId={destinationCodes[0]}
                startDate={startDate}
                data={[]}
                loadMore={() => {}}
                loading={false}
                view={''}
                selectedItems={selectedItems}
                setSelectedItems={setSelectedItems}
                addTrain={addTrain}
                removeTrain={removeTrain}
                isTrainSelected={isTrainSelected}
            />
        </div>
    );
};

export default TravelDetails;
