import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../css/MyPlanDetail.css'; // CSS 파일 호출
import Kakao from '../img/Kakao.png'; // 카카오톡 아이콘 이미지

function MyPlanDetail() {
  const { state } = useLocation();
  const [plan, setPlan] = useState(state && state.plan);
  const [newTodo, setNewTodo] = useState('');
  const [details, setDetails] = useState({
    accommodations: [],
    tours: [],
    restaurants: [],
    transportations: []
  });

  useEffect(() => {
    if (plan && plan.todo) {
      setNewTodo(plan.todo);
    }
  }, [plan]);

  useEffect(() => {
    async function fetchPlanDetails() {
      if (!plan || !plan.saveNo) return;
      try {
        const response = await axios.get(`/api/users/${plan.saveNo}`);
        const data = response.data;

        setDetails({
          accommodations: data.accommodations,
          tours: data.tours,
          restaurants: data.restaurants,
          transportations: data.transportations
        });
      } catch (error) {
        console.error('Error fetching additional data:', error);
      }
    }

    fetchPlanDetails();
  }, [plan]);

  useEffect(() => {
    if (!window.Kakao.isInitialized()) {
      window.Kakao.init('0912fbb913424b3216c43753395338bd'); // 여기에 본인의 카카오 앱 키를 입력하세요
    }
  }, []);

  const handleUpdateTodo = async () => {
    try {
      await axios.put(`/api/users/${plan.saveNo}/todo`, { todo: newTodo });
      setPlan({ ...plan, todo: newTodo });
    } catch (error) {
      console.error('Failed to update todo:', error);
    }
  };

  const getAccommodationsText = () => {
    return details.accommodations.map(acc => acc.accName).join(', ');
  };

  const getRestaurantsText = () => {
    return details.restaurants.map(res => res.resName).join(', ');
  };

  const getTransportationsText = () => {
    return details.transportations.map(trans => trans.transName).join(', ');
  };

  const getToursText = () => {
    return details.tours.map(tour => tour.tourName).join(', ');
  };

  const handleKakaoShare = () => {
    const descriptionText = `
      여행 기간: ${plan.firstDate} - ${plan.endDate}\n
      출발지: ${plan.firstPlace}\n
      도착지: ${plan.endPlace}\n
      숙박: ${getAccommodationsText()}\n
      레스토랑: ${getRestaurantsText()}\n
      교통: ${getTransportationsText()}\n
      관광: ${getToursText()}\n
      할일: ${plan.todo}
    `;

    window.Kakao.Link.sendDefault({
      objectType: 'feed',
      content: {
        title: `여행 계획: ${plan.saveTitle}`,
        description: descriptionText.substring(0, 100) + '...', // 최대 100자로 잘라 요약본 표시
        imageUrl: '이미지 URL', // 공유할 이미지 URL을 추가하세요
        link: {
          mobileWebUrl: window.location.href,
          webUrl: window.location.href
        }
      },
      social: {
        likeCount: 0,
        commentCount: 0,
        sharedCount: 0
      },
      buttons: [
        {
          title: '자세히 보기',
          link: {
            mobileWebUrl: window.location.href,
            webUrl: window.location.href
          }
        }
      ]
    });
  };

  return (
    <div>
      <div className="myplandetail-container">
        <div className="myplandetail-card">
          <h2 className="myplandetail-text-center">나의 여행 계획 상세</h2>
          {plan && (
            <>
              <div className="myplandetail-section">
                <h3>저장 제목</h3>
                <p>{plan.saveTitle}</p>
              </div>
              <div className="myplandetail-section">
                <h3>여행 시작 날짜</h3>
                <p>{plan.firstDate}</p>
              </div>
              <div className="myplandetail-section">
                <h3>여행 종료 날짜</h3>
                <p>{plan.endDate}</p>
              </div>
              <div className="myplandetail-section">
                <h3>출발지</h3>
                <p>{plan.firstPlace}</p>
              </div>
              <div className="myplandetail-section">
                <h3>도착지</h3>
                <p>{plan.endPlace}</p>
              </div>
            </>
          )}
          {plan && (
            <>
              {details.accommodations.length > 0 && (
                <div className="myplandetail-section">
                  <h3>숙박 정보</h3>
                  {details.accommodations.map((acc, index) => (
                    <div key={index}>
                      <p>{acc.accName}</p>
                      <p>{acc.accAddress}</p>
                      <p>{acc.accImg}</p>
                      <p>{acc.accTel}</p>
                    </div>
                  ))}
                </div>
              )}

              {details.restaurants.length > 0 && (
                <div className="myplandetail-section">
                  <h3>레스토랑 정보</h3>
                  {details.restaurants.map((res, index) => (
                    <div key={index}>
                      <p>{res.resName}</p>
                      <p>{res.resAddress}</p>
                      <p>{res.resImg}</p>
                      <p>{res.resTel}</p>
                    </div>
                  ))}
                </div>
              )}

              {details.transportations.length > 0 && (
                <div className="myplandetail-section">
                  <h3>교통 수단 정보</h3>
                  {details.transportations.map((trans, index) => (
                    <div key={index}>
                      <p>{trans.transName}</p>
                      <p>{trans.firstPlace} → {trans.endPlace}</p>
                      <p>{trans.time}</p>
                    </div>
                  ))}
                </div>
              )}

              {details.tours.length > 0 && (
                <div className="myplandetail-section">
                  <h3>관광 정보</h3>
                  {details.tours.map((tour, index) => (
                    <div key={index}>
                      <p>{tour.tourName}</p>
                      <p>{tour.tourAddress}</p>
                      <p>{tour.tourImg}</p>
                    </div>
                  ))}
                </div>
              )}
            </>
          )}

          <div className="myplandetail-section">
            <h3>할일</h3>
            <p>{plan.todo}</p>
            <input
              type="text"
              className="form-control"
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
            />
            <button className="myplandetail-btn btn btn-primary" onClick={handleUpdateTodo}>입력</button>
          </div>
          <button className="myplandetail-btn btn btn-primary" onClick={handleKakaoShare}>
            <img src={kakao} alt="Kakao" style={{ width: '20px', marginRight: '5px' }} /> {/* 카카오톡 아이콘 삽입 */}
            카카오톡 공유하기
          </button>
        </div>
      </div>
    </div>
  );
}

export default MyPlanDetail;
