import React, { useState, useEffect } from 'react';

const Footer = () => {
  const [weatherData, setWeatherData] = useState([]);

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const cities = ['Seoul', 'Busan', 'Incheon', 'Daegu', 'Daejeon', 'Gwangju', 'Ulsan', 'Suwon', 'Changwon', 'Seongnam', 'Goyang', 'Yongin', 'Seoul', 'Gimpo', 'Pohang', 'Ansan', 'Jeonju'];
        const promises = cities.map(city =>
            fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},KR&appid=d0e510d19e9d8edffb59825460db4868&units=metric`)
                .then(response => response.json())
                .then(data => ({ city, temperature: data.main.temp }))
        );
        const weatherResults = await Promise.all(promises);
        setWeatherData(weatherResults);
      } catch (error) {
        console.error('Error fetching weather data:', error);
      }
    };

    fetchWeatherData();
  }, []);

  return (
      <footer className="container footer-flow">
        <div className="row">
          {weatherData.map(({ city, temperature }) => (
              <div key={city} className="col">
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{city}</h5>
                    <p className="card-text">{temperature}Â°C</p>
                  </div>
                </div>
              </div>
          ))}
        </div>
      </footer>
  );
};

export default Footer;
