import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [loginId, setLoginId] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // 로그인 실패 시 에러 메시지를 저장하는 상태

    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await axios.post('/api/users/login', {
                loginId: loginId,
                pw: password
            });

            if (response.data.success) {
                console.log('로그인 성공:', response.data);
                navigate('/');
            } else {
                console.error('로그인 실패:', response.data);
                setError('아이디 또는 비밀번호가 올바르지 않습니다.'); // 로그인 실패 시 에러 메시지 설정
            }
        } catch (error) {
            console.error('로그인 요청 실패:', error);
            setError('로그인 요청 중 오류가 발생했습니다.'); // 로그인 요청 실패 시 에러 메시지 설정
        }
    };


    return (
        <div>
            <h2>로그인</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>} {/* 로그인 실패 시 에러 메시지 표시 */}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>아이디:</label>
                    <input
                        type="text"
                        name="login_id"
                        value={loginId}
                        onChange={(e) => setLoginId(e.target.value)}
                    />
                </div>
                <div>
                    <label>비밀번호:</label>
                    <input
                        type="password"
                        name="pw"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                <button type="submit">로그인</button>
            </form>
        </div>
    );
};

export default Login;
