import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../css/MyPlanDetail.css'; // CSS 파일 호출

function MyPlanDetail() {
  const { state } = useLocation();
  const [plan, setPlan] = useState(state && state.plan);
  const [newTodo, setNewTodo] = useState('');

  useEffect(() => {
    async function fetchPlan() {
      try {
        const response = await axios.get(`/api/users/${plan.saveNo}`); // 백엔드에서 해당 계획의 상세 정보와 다른 테이블의 정보를 가져오는 API 호출
        setPlan(response.data); // 가져온 정보로 plan 객체 업데이트
      } catch (error) {
        console.error('여행 계획 상세 정보를 불러오는 중 오류 발생:', error);
      }
    }

    if (plan) {
      fetchPlan();
    }
  }, [plan]); // plan 객체가 변경될 때마다 호출

  const handleUpdateTodo = async () => {
    try {
      await axios.put(`/api/users/${plan.saveNo}/todo`, { todo: newTodo });
      // 새로운 할일을 업데이트한 후에 즉시 UI에 반영
      setPlan({ ...plan, todo: newTodo });
    } catch (error) {
      console.error('할일 업데이트 실패:', error);
      // 실패 시 필요한 처리
    }
  };
useEffect(() => {
  async function fetchAdditionalData() {
    try {
      const accommodationResponse = await axios.get(`/api/accommodations/${plan.saveNo}`);
      const restaurantResponse = await axios.get(`/api/restaurants/${plan.saveNo}`);
      const transportationResponse = await axios.get(`/api/transportations/${plan.saveNo}`);
      const tourResponse = await axios.get(`/api/tours/${plan.saveNo}`);

      const accommodationData = accommodationResponse.data;
      const restaurantData = restaurantResponse.data;
      const transportationData = transportationResponse.data;
      const tourData = tourResponse.data;

      // 가져온 데이터를 상태에 설정
      // 예: setAccommodation(accommodationData);
      // setRestaurant(restaurantData);
      // setTransportation(transportationData);
      // setTour(tourData);
    } catch (error) {
      console.error('Error fetching additional data:', error);
    }
  }

  if (plan) {
    fetchAdditionalData();
  }
}, [plan]);

  return (
      <div className="myplandetail-container">
        <div className="myplandetail-card">
          <h2 className="myplandetail-text-center">나의 여행 계획 상세</h2>
          {plan && (
              <>
                <div className="myplandetail-section">
                  <h3>저장 제목</h3>
                  <p>{plan.saveTitle}</p>
                </div>
                <div className="myplandetail-section">
                  <h3>여행 시작 날짜</h3>
                  <p>{plan.firstDate}</p>
                </div>
                <div className="myplandetail-section">
                  <h3>여행 종료 날짜</h3>
                  <p>{plan.endDate}</p>
                </div>
                <div className="myplandetail-section">
                  <h3>출발지</h3>
                  <p>{plan.firstPlace}</p>
                </div>
                <div className="myplandetail-section">
                  <h3>도착지</h3>
                  <p>{plan.endPlace}</p>
                </div>
                <div className="myplandetail-section">
                  <h3>할일</h3>
                  <p>{plan.todo}</p>
                  <input
                      type="text"
                      className="form-control"
                      value={newTodo}
                      onChange={(e) => setNewTodo(e.target.value)}
                      placeholder="새로운 할일 입력"
                  />
                  <button className="myplandetail-btn btn btn-primary" onClick={handleUpdateTodo}>입력</button>
                </div>
                {/* 숙박 정보 표시 */}
                {plan.accommodation && (
                  <div className="myplandetail-section">
                    <h3>숙박 정보</h3>
                    <p>{plan.accommodation.accName}</p>
                    <p>{plan.accommodation.accAddress}</p>
                    {/* 나머지 필드도 동일한 방식으로 표시 */}
                  </div>
                )}
                {/* 레스토랑 정보 표시 */}
                {plan.restaurant && (
                  <div className="myplandetail-section">
                    <h3>레스토랑 정보</h3>
                    <p>{plan.restaurant.resName}</p>
                    <p>{plan.restaurant.resAddress}</p>
                    {/* 나머지 필드도 동일한 방식으로 표시 */}
                  </div>
                )}
                {/* 교통 수단 정보 표시 */}
                {plan.transportation && (
                  <div className="myplandetail-section">
                    <h3>교통 수단 정보</h3>
                    <p>{plan.transportation.transName}</p>
                    <p>{plan.transportation.firstPlace} → {plan.transportation.endPlace}</p>
                    {/* 나머지 필드도 동일한 방식으로 표시 */}
                  </div>
                )}
                {/* 관광 정보 표시 */}
                {plan.tour && (
                  <div className="myplandetail-section">
                    <h3>관광 정보</h3>
                    <p>{plan.tour.tourName}</p>
                    <p>{plan.tour.tourAddress}</p>
                    {/* 나머지 필드도 동일한 방식으로 표시 */}
                  </div>
                )}
              </>
          )}
        </div>
      </div>
  );
}

export default MyPlanDetail;
