import React from 'react';
import { useLocation } from 'react-router-dom';

import LocationBasedList from './LocationBasedList';
import TrainList from './TrainList';

import DummyLocationBasedList from './DummyLocationBasedList';


const TravelDetails = () => {
    const location = useLocation();
    const params = new URLSearchParams(location.search);
    const startDate = params.get('startDate');
    const endDate = params.get('endDate');
    let departure = params.get('departure');
    let destination = params.get('destination');
    let destination2 = params.get('destination');

    const regionMapping = {
        "서울": "1",
        "인천": "2",
        "대전": "3",
        "대구": "4",
        "광주": "5",
        "부산": "6",
        "울산": "7",
        "세종": "8",
        "경기도": "31",
        "강원도": "32",
        "충청북도": "33",
        "충청남도": "34",
        "경상북도": "35",
        "경상남도": "36",
        "전라북도": "37",
        "전라남도": "38",
        "제주도": "39"
    };
    const regionMapping1 = {
           "서울": ["NAT010000", "NAT010091", "NAT010032"],
           "부산": ["NAT014445"],
           "대구": ["NAT013239", "NAT013271", "NAT013189"],
           "인천": [""],
           "광주": ["NAT031857", "NAT883012", "NAT882936"],
           "대전": ["NAT011668", "NAT030057", "NAT011524"],
           "울산": ["NAT750781", "NATH13717"],
           "세종": ["NAT012345"],
           "경기도": ["NAT110147", "NAT010415", "NAT010239", "NATH30326"],
           "강원도": ["NAT020864", "NAT020947", "NAT140873", "NAT601936",
                     "NATN10230", "NATN10625", "NAT650978"],
           "충청북도": ["NAT030254"],
           "충청남도": ["NAT030254", "NAT030508", "NATH10960", "NATH20438"],
           "전라북도": ["NAT030879", "NAT031314", "NAT040257", "NAT040868"],
           "전라남도": ["NAT031998", "NAT032563", "NAT041072", ""],
           "경상북도": ["NAT080000"],
           "경상남도": ["NAT090000"],
           "제주도": ["NAT100000"]
       };


    const departureCodes = regionMapping1[departure] || [];
    const destinationCodes = regionMapping1[destination] || [];



    // destination 값을 rnum 값으로 변환
    if (regionMapping[destination2]) {
        destination2 = regionMapping[destination2];
    }

    return (
        <div>
            <h2>Travel Details</h2>
            <p><strong>Departure:</strong> {departure}</p>
            <p><strong>Destination:</strong> {destination}</p>
            <p><strong>Start Date:</strong> {startDate}</p>
            <p><strong>End Date:</strong> {endDate}</p>

           {departureCodes.map(depCode =>
                          destinationCodes.map(destCode => (
                              <TrainList
                                  key={`${depCode}-${destCode}`}
                                  depPlaceId={depCode}
                                  arrPlaceId={destCode}
                                  startDate={startDate}/>)))}
            <LocationBasedList destination={destination} />
            <DummyLocationBasedList destination2={destination2} />

        </div>
    );
};

export default TravelDetails;
