import React, { useState } from 'react';
import '../css/RecommendedPlaces.css';
import LoadMoreButton from './LoadMoreButton';

const RecommendedPlaces = ({ data, loadMore, loading }) => {



const [selectedItems, setSelectedItems] = useState({
  attractions: [],
  hotels: [],
  restaurants: []
});

const handleAddClick = (item) => {
  const { contenttypeid } = item;
  const existingItem = selectedItems[contenttypeid].find(selectedItem => selectedItem.contentid === item.contentid);
  if (!existingItem) {
    const newItem = { ...item, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) };
    setSelectedItems(prevState => ({
      ...prevState,
      [contenttypeid]: [...prevState[contenttypeid], newItem]
    }));
  }
};

const handleRemoveClick = (contentid, contenttypeid) => {
  setSelectedItems(prevState => ({
    ...prevState,
    [contenttypeid]: prevState[contenttypeid].filter(item => item.contentid !== contentid)
  }));
};

const isItemSelected = (contentid, contenttypeid) => {
  return selectedItems[contenttypeid].some(item => item.contentid === contentid);
};


  return (
    <div>
      <div className="card-container">
        {data.map((item, index) => (
          <div key={item.contentid + index} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="hotel-image"
            />
            {isItemSelected(item.contentid) ? (
              <button onClick={() => handleRemoveClick(item.contentid)}>체크</button>
            ) : (
              <button onClick={() => handleAddClick(item)}>추가</button>
            )}
          </div>
        ))}
      </div>
      <LoadMoreButton loadMore={loadMore} loading={loading} />
      <div>
  <h1>Selected Attractions</h1>
  <div className="selected-items">
    {selectedItems.attractions.map((item) => (
      <div key={item.uniqueId} className="card">
        <h2>{item.title}</h2>
        <p>{item.addr1}</p>
        <img
          src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={item.title}
          className="selected-image"
        />
        <button onClick={() => handleRemoveClick(item.contentid, 'attractions')}>삭제</button>
      </div>
    ))}
  </div>

  <h1>Selected Hotels</h1>
  <div className="selected-items">
    {selectedItems.hotels.map((item) => (
      <div key={item.uniqueId} className="card">
        <h2>{item.title}</h2>
        <p>{item.addr1}</p>
        <img
          src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={item.title}
          className="selected-image"
        />
        <button onClick={() => handleRemoveClick(item.contentid, 'hotels')}>삭제</button>
      </div>
    ))}
  </div>

  <h1>Selected Restaurants</h1>
  <div className="selected-items">
    {selectedItems.restaurants.map((item) => (
      <div key={item.uniqueId} className="card">
        <h2>{item.title}</h2>
        <p>{item.addr1}</p>
        <img
          src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={item.title}
          className="selected-image"
        />
        <button onClick={() => handleRemoveClick(item.contentid, 'restaurants')}>삭제</button>
      </div>
    ))}
  </div>
</div>

    </div>
  );
};

export default RecommendedPlaces;
