import React, { useState } from 'react';
import '../css/RecommendedPlaces.css';
import LoadMoreButton from './LoadMoreButton';

const RecommendedPlaces = ({ data, loadMore, loading }) => {
  const [selectedItems1, setSelectedItems1] = useState([]);
  const [selectedItems2, setSelectedItems2] = useState([]);
  const [selectedItems3, setSelectedItems3] = useState([]);

  const handleAddClick = (item, listIndex) => {
    const existingItem = listIndex === 1 ? selectedItems1.find(selectedItem => selectedItem.contentid === item.contentid) : 
                         listIndex === 2 ? selectedItems2.find(selectedItem => selectedItem.contentid === item.contentid) : 
                         selectedItems3.find(selectedItem => selectedItem.contentid === item.contentid);
    if (!existingItem) {
      const newItem = { ...item, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) };
      if (listIndex === 1) setSelectedItems1([...selectedItems1, newItem]);
      else if (listIndex === 2) setSelectedItems2([...selectedItems2, newItem]);
      else setSelectedItems3([...selectedItems3, newItem]);
    }
  };

  const handleRemoveClick = (contentid, listIndex) => {
    if (listIndex === 1) setSelectedItems1(selectedItems1.filter(item => item.contentid !== contentid));
    else if (listIndex === 2) setSelectedItems2(selectedItems2.filter(item => item.contentid !== contentid));
    else setSelectedItems3(selectedItems3.filter(item => item.contentid !== contentid));
  };

  const isItemSelected = (contentid, listIndex) => {
    const selectedItems = listIndex === 1 ? selectedItems1 : listIndex === 2 ? selectedItems2 : selectedItems3;
    return selectedItems.some(item => item.contentid === contentid);
  };

  return (
    <div>
      <div className="card-container">
        {data.map((item, index) => (
          <div key={item.contentid + index} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="hotel-image"
            />
            <button onClick={() => handleAddClick(item, 1)}>추가1</button>
            <button onClick={() => handleAddClick(item, 2)}>추가2</button>
            <button onClick={() => handleAddClick(item, 3)}>추가3</button>
          </div>
        ))}
      </div>
      <LoadMoreButton loadMore={loadMore} loading={loading} />
      <h1>Selected Places 1</h1>
      <div className="selected-items">
        {selectedItems1.map((item) => (
          <div key={item.uniqueId} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <input type='hidden' value={item.contenttypeid}></input>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="selected-image"
            />
            <button onClick={() => handleRemoveClick(item.contentid, 1)}>삭제</button>
          </div>
        ))}
      </div>
      <h1>Selected Places 2</h1>
      <div className="selected-items">
        {selectedItems2.map((item) => (
          <div key={item.uniqueId} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <input type='hidden' value={item.contenttypeid}></input>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="selected-image"
            />
            <button onClick={() => handleRemoveClick(item.contentid, 2)}>삭제</button>
          </div>
        ))}
      </div>
      <h1>Selected Places 3</h1>
      <div className="selected-items">
        {selectedItems3.map((item) => (
          <div key={item.uniqueId} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <input type='hidden' value={item.contenttypeid}></input>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="selected-image"
            />
            <button onClick={() => handleRemoveClick(item.contentid, 3)}>삭제</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedPlaces;
