// MainLayout.js

import React, { useState, useEffect } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Footer from './Footer';
import Login from './Login'; // 로그인 컴포넌트를 불러옵니다.

const MainLayout = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 상태를 관리합니다.
    const navigate = useNavigate();

    const handleLogout = () => {
        axios.post('/api/users/logout')
            .then(() => {
                setIsLoggedIn(false);
                navigate('/login');
            })
            .catch(error => {
                console.error('로그아웃 실패:', error);
            });
    };

    useEffect(() => {
        // 로그인 상태를 확인하는 로직 추가 (예: API 호출)
        // setIsLoggedIn(true or false); 로 로그인 상태를 설정
    }, []);

    return (
        <div className="App">
            <header className="container">
                <div className="col">
                    <h1>YOUR PLANNER</h1>
                    <h4>유플과 시작하는 여행 !</h4>
                </div>
                <div className="row">
                    <div className="col">
                        <div className="auth-links">
                            {isLoggedIn ? (
                                <>
                                    <button onClick={handleLogout}>로그아웃</button>
                                </>
                            ) : (
                                <>
                                    <Link to="/login">로그인</Link> | <Link to="/signup">회원가입</Link>
                                </>
                            )}
                        </div>
                    </div>
                </div>
            </header>
            <section className="container">
                <div className="row">
                    <div className="col">
                        {/* Login 컴포넌트를 렌더링하고 setIsLoggedIn을 prop으로 전달합니다. */}
                        <Login setIsLoggedIn={setIsLoggedIn} />
                        <Outlet />
                    </div>
                </div>
            </section>
            <Footer />
        </div>
    );
};

export default MainLayout;
