import React from 'react';
import '../css/RecommendedPlaces.css';
import LoadMoreButton from './LoadMoreButton';

const RecommendedPlaces = ({ data, loadMore, loading, view, selectedItems, setSelectedItems }) => {

  const handleRemoveClick = (contentid, category) => {
    setSelectedItems(prevState => ({
      ...prevState,
      [category]: prevState[category].filter(item => item.contentid !== contentid)
    }));
  };

  const formatDate = (datetime) => {
    if (typeof datetime !== 'string') {
      datetime = datetime.toString();
    }
    const year = datetime.substring(0, 4);
    const month = datetime.substring(4, 6);
    const day = datetime.substring(6, 8);
    const hour = datetime.substring(8, 10);
    const minute = datetime.substring(10, 12);
    return `${month}월 ${day}일 ${hour}시 ${minute}분`;
  };

  const removeDuplicates = (items) => {
    const uniqueItems = items.reduce((acc, current) => {
      const x = acc.find(item => item.contentid === current.contentid);
      if (!x) {
        return acc.concat([current]);
      } else {
        return acc;
      }
    }, []);
    return uniqueItems;
  };

  return (
    <div>
      <div className="card-container">
        {data.map((item, index) => {
          let category;
          switch (item.contenttypeid) {
            case '12':
              category = 'attractions';
              break;
            case '32':
              category = 'hotels';
              break;
            case '39':
              category = 'restaurants';
              break;
            default:
              return null;
          }
          return (
            <div key={item.contentid + index} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="hotel-image"
              />
            </div>
          );
        })}
      </div>
      <LoadMoreButton loadMore={loadMore} loading={loading} />
      <div>
        <h1>Selected Attractions</h1>
        <div className="selected-items">
          {removeDuplicates(selectedItems.attractions).map((item) => (
            <div key={item.uniqueId} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="selected-image"
              />
              <button onClick={() => handleRemoveClick(item.contentid, 'attractions')}>삭제</button>
            </div>
          ))}
        </div>

        <h1>Selected Hotels</h1>
        <div className="selected-items">
          {removeDuplicates(selectedItems.hotels).map((item) => (
            <div key={item.uniqueId} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="selected-image"
              />
              <button onClick={() => handleRemoveClick(item.contentid, 'hotels')}>삭제</button>
            </div>
          ))}
        </div>

        <h1>Selected Restaurants</h1>
        <div className="selected-items">
          {removeDuplicates(selectedItems.restaurants).map((item) => (
            <div key={item.uniqueId} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="selected-image"
              />
              <button onClick={() => handleRemoveClick(item.contentid, 'restaurants')}>삭제</button>
            </div>
          ))}
        </div>

        <h1>Selected Trains</h1>
        <div className="selected-items">
          {removeDuplicates(selectedItems.trains).map((train) => (
            <div key={train.uniqueId} className="card">
              <h2>{train.traingradename}</h2>
              <p>출발: {train.depplacename}역 {formatDate(train.depplandtime)}</p>
              <p>도착: {train.arrplacename}역 {formatDate(train.arrplandtime)}</p>
              <p>요금: {train.adultcharge}원</p>
              <p>열차번호: {train.trainno}</p>
              <button onClick={() => handleRemoveClick(train.trainno, 'trains')}>삭제</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RecommendedPlaces;
