import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import '../css/MyPlanDetail.css'; // CSS 파일 호출

function MyPlanDetail() {
  const { state } = useLocation();
  const [plan, setPlan] = useState(state && state.plan);
  const [newTodo, setNewTodo] = useState('');
  const [accommodation, setAccommodation] = useState(null);
  const [restaurant, setRestaurant] = useState(null);
  const [transportation, setTransportation] = useState(null);
  const [tour, setTour] = useState(null);

  useEffect(() => {
    async function fetchAdditionalData() {
      try {
        const accommodationResponse = await axios.get(`/api/accommodations/${plan.saveNo}`);
        const restaurantResponse = await axios.get(`/api/restaurants/${plan.saveNo}`);
        const transportationResponse = await axios.get(`/api/transportations/${plan.saveNo}`);
        const tourResponse = await axios.get(`/api/tours/${plan.saveNo}`);

        setAccommodation(accommodationResponse.data);
        setRestaurant(restaurantResponse.data);
        setTransportation(transportationResponse.data);
        setTour(tourResponse.data);
      } catch (error) {
        console.error('Error fetching additional data:', error);
      }
    }

    if (plan) {
      fetchAdditionalData();
    }
  }, [plan]);

  const handleUpdateTodo = async () => {
    try {
      await axios.put(`/api/users/${plan.saveNo}/todo`, { todo: newTodo });
      setPlan({ ...plan, todo: newTodo });
    } catch (error) {
      console.error('Failed to update todo:', error);
    }
  };

  return (
    <div className="myplandetail-container">
      <div className="myplandetail-card">
        <h2 className="myplandetail-text-center">나의 여행 계획 상세</h2>
        {plan && (
          <>
            <div className="myplandetail-section">
              <h3>저장 제목</h3>
              <p>{plan.saveTitle}</p>
            </div>
            <div className="myplandetail-section">
              <h3>여행 시작 날짜</h3>
              <p>{plan.firstDate}</p>
            </div>
            <div className="myplandetail-section">
              <h3>여행 종료 날짜</h3>
              <p>{plan.endDate}</p>
            </div>
            <div className="myplandetail-section">
              <h3>출발지</h3>
              <p>{plan.firstPlace}</p>
            </div>
            <div className="myplandetail-section">
              <h3>도착지</h3>
              <p>{plan.endPlace}</p>
            </div>
            <div className="myplandetail-section">
              <h3>할일</h3>
              <p>{plan.todo}</p>
              <input
                type="text"
                className="form-control"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="새로운 할일 입력"
              />
              <button className="myplandetail-btn btn btn-primary" onClick={handleUpdateTodo}>입력</button>
            </div>


              <div className="myplandetail-section">
                <h3>숙박 정보</h3>
                <p>{accommodation.accName}</p>
                <p>{accommodation.accAddress}</p>
              </div>

            {/* 레스토랑 정보 표시 */}
            {restaurant && (
              <div className="myplandetail-section">
                <h3>레스토랑 정보</h3>
                <p>{restaurant.resName}</p>
                <p>{restaurant.resAddress}</p>
              </div>
            )}
            {/* 교통 수단 정보 표시 */}
            {transportation && (
              <div className="myplandetail-section">
                <h3>교통 수단 정보</h3>
                <p>{transportation.transName}</p>
                <p>{transportation.firstPlace} → {transportation.endPlace}</p>
              </div>
            )}
            {/* 관광 정보 표시 */}
            {tour && (
              <div className="myplandetail-section">
                <h3>관광 정보</h3>
                <p>{tour.tourName}</p>
                <p>{tour.tourAddress}</p>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default MyPlanDetail;
