import React, { useEffect } from 'react';
import '../css/RecommendedPlaces.css';

const RecommendedPlaces = ({ data, selectedItems, onAddItemClick, onRemoveItemClick }) => {
  useEffect(() => {
    <div className="selected-items">
    {selectedItems && selectedItems.length > 0 && selectedItems.map((item, index) => (
      <div key={index} className="card">
        <h2>{item.title}</h2>
        <p>{item.addr1}</p>
        <img
          src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
          alt={item.title}
          className="selected-image"
        />
      </div>
    ))}
  </div>
  }, [selectedItems]);

  return (
    <div>
      <div className="card-container">
        {data && data.length > 0 && data.map((item, index) => (
          <div key={index} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="hotel-image"
            />
            <button onClick={() => onAddItemClick(item)}>추가</button>
          </div>
        ))}
      </div>
      <h1>Selected Places</h1>
      <div className="selected-items">
        {selectedItems && selectedItems.length > 0 && selectedItems.map((item, index) => (
          <div key={index} className="card">
            <h2>{item.title}</h2>
            <p>{item.addr1}</p>
            <img
              src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
              alt={item.title}
              className="selected-image"
            />
            <button onClick={() => onRemoveItemClick(item)}>삭제</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedPlaces;
