import React, { useState, useEffect } from 'react';

const LocationBasedList = ({ destination }) => {
  const [selectedLocation, setSelectedLocation] = useState(destination);
  const [selectedSigungu, setSelectedSigungu] = useState('');
  const [locations, setLocations] = useState([]);
  const [sigungus, setSigungus] = useState([destination]);
  const OPEN_KEY = "5uJ1mFn4tOfEwReTW3dupjd4w2n5kEHO5nciT%2BDVGAVWTl90sysBKbMTIlIxLW5lCPo1VmpZ%2FXggxU84GhG81g%3D%3D";

  useEffect(() => {
    const fetchLocations = async () => {
      try {
        const response = await fetch(
          `https://apis.data.go.kr/B551011/KorService1/areaCode1?serviceKey=${OPEN_KEY}&numOfRows=17&pageNo=1&MobileOS=ETC&MobileApp=AppTest&_type=json`
        );

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const json = await response.json();
        setLocations(json.response.body.items.item);
      } catch (error) {
        console.error('Fetch error:', error);
      }
    };

    fetchLocations();
  }, []);

  useEffect(() => {
    const fetchSigungus = async () => {
      if (!selectedLocation) return;

      try {
        const response = await fetch(
          `http://apis.data.go.kr/B551011/KorService1/areaBasedList1?serviceKey=${OPEN_KEY}&numOfRows=1000&MobileApp=AppTest&MobileOS=ETC&arrange=A&areaCode=${selectedLocation}&sigunguCode&_type=json`
        );

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const json = await response.json();
        setSigungus(json.response.body.items.item);
      } catch (error) {
        console.error('Fetch error:', error);
      }
    };

    fetchSigungus();
  }, [selectedLocation]);

  const handleLocationChange = (event) => {
    setSelectedLocation(event.target.value);
    setSelectedSigungu('');
  };

  const handleSigunguChange = (event) => {
    setSelectedSigungu(event.target.value);
  };

  return (
    <div>
      <select value={selectedLocation} onChange={handleLocationChange}>
        <option value="">지역을 선택하세요</option>
        {locations.map((location, index) => (
          <option key={index} value={location.code}>
            {location.name}
          </option>
        ))}
      </select>

      {selectedLocation && (
        <select value={selectedSigungu} onChange={handleSigunguChange}>
          <option value="">시/군/구를 선택하세요</option>
          {sigungus.map((sigungu, index) => (
            <option key={index} value={sigungu.sigunguCode}>
              {sigungu.name}
            </option>
          ))}
        </select>
      )}
    </div>
  );
};

export default LocationBasedList;
