import React from 'react';
import '../css/RecommendedPlaces.css';
import LoadMoreButton from './LoadMoreButton';

const RecommendedPlaces = ({ data, loadMore, loading, selectedItems, setSelectedItems, selectedTrains, setSelectedTrains }) => {

  const handleAddClick = (item) => {
    let category;
    switch (item.contenttypeid) {
      case '12':
        category = 'attractions';
        break;
      case '32':
        category = 'hotels';
        break;
      case '39':
        category = 'restaurants';
        break;
      default:
        return;
    }

    if (!selectedItems[category]) {
      setSelectedItems(prevState => ({
        ...prevState,
        [category]: []
      }));
    }

    const existingItem = selectedItems[category]?.find(selectedItem => selectedItem.contentid === item.contentid);
    if (!existingItem) {
      const newItem = { ...item, uniqueId: Date.now() + Math.random().toString(36).substr(2, 9) };
      setSelectedItems(prevState => ({
        ...prevState,
        [category]: [...(prevState[category] || []), newItem]
      }));
    }
  };

  const handleRemoveClick = (contentid, category) => {
    setSelectedItems(prevState => ({
      ...prevState,
      [category]: prevState[category].filter(item => item.contentid !== contentid)
    }));
  };

  const handleRemoveTrainClick = (trainno) => {
    setSelectedTrains(prevState => prevState.filter(train => train.trainno !== trainno));
  };

  const isItemSelected = (contentid, category) => {
    return selectedItems[category] && selectedItems[category].some(item => item.contentid === contentid);
  };

  const formatDate = (datetime) => {
    if (typeof datetime !== 'string') {
      datetime = datetime.toString();
    }
    const year = datetime.substring(0, 4);
    const month = datetime.substring(4, 6);
    const day = datetime.substring(6, 8);
    const hour = datetime.substring(8, 10);
    const minute = datetime.substring(10, 12);
    return `${month}월 ${day}일 ${hour}시 ${minute}분`;
  };

  return (
    <div>
      <div className="card-container">
        {data.map((item, index) => {
          let category;
          switch (item.contenttypeid) {
            case '12':
              category = 'attractions';
              break;
            case '32':
              category = 'hotels';
              break;
            case '39':
              category = 'restaurants';
              break;
            default:
              return null;
          }
          return (
            <div key={item.contentid + index} className="card">
              <h2>{item.title}</h2>
              <p>{item.addr1}</p>
              <img
                src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.title}
                className="hotel-image"
              />
              {isItemSelected(item.contentid, category) ? (
                <button onClick={() => handleRemoveClick(item.contentid, category)}>체크</button>
              ) : (
                <button onClick={() => handleAddClick(item)}>추가</button>
              )}
            </div>
          );
        })}
      </div>
      {loading ? (
        <p>로딩 중...</p>
      ) : (
        <LoadMoreButton loadMore={loadMore} />
      )}
      <div>
        <h1>Selected Items</h1>
        {Object.keys(selectedItems).map(category => (
          <div key={category}>
            <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
            <div className="selected-items">
              {selectedItems[category].map(item => (
                <div key={item.uniqueId} className="card">
                  <h2>{item.title}</h2>
                  <p>{item.addr1}</p>
                  <img
                    src={item.firstimage2 || 'https://via.placeholder.com/300x200?text=No+Image'}
                    alt={item.title}
                    className="hotel-image"
                  />
                  <button onClick={() => handleRemoveClick(item.contentid, category)}>삭제</button>
                </div>
              ))}
            </div>
          </div>
        ))}
        <h1>Selected Trains</h1>
        <div className="selected-items">
          {selectedTrains.map((train) => (
            <div key={train.uniqueId} className="card">
              <p>{train.traingradename} / 출발: {train.depplacename}역 {formatDate(train.depplandtime)} / 도착: {train.arrplacename}역 {formatDate(train.arrplandtime)} / 요금: {train.adultcharge}원 / 열차번호: {train.trainno}</p>
              <button onClick={() => handleRemoveTrainClick(train.trainno)}>삭제</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RecommendedPlaces;
